import React, { Component, Fragment, useState } from "react";
import './Donate.css';



const DonationForm = () => {
    const [donationType, setDonationType] = useState('one-time');
    const [amount, setAmount] = useState('');

    const handleDonationChange = (e) => {
        setDonationType(e.target.value);
    };

    const handleAmountChange = (e) => {
        setAmount(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        alert(`Donation type: ${donationType}, Amount: £${amount}`);
    };

    return (
        <form onSubmit={handleSubmit} className="donation-form">
            <h3 className="form-title">Support our work</h3>
            <div className="donation-type">
                
                <label>
                    <input
                        type="radio"
                        name="donationType"
                        value="one-time"
                        checked={donationType === 'one-time'}
                        onChange={handleDonationChange}
                       
                    />
                     <span> One-off donation</span>
                  
                </label>
                <label>
                    <input
                        type="radio"
                        name="donationType"
                        value="monthly"
                        checked={donationType === 'monthly'}
                        onChange={handleDonationChange}
                    />
                    <span className="donate">I'll keep supporting your work for Palestine - make my donation monthly</span>
                </label>
            </div>

            <h4 className="amount-title">Choose your amount:</h4>
            <div className="amount-options">

                {[ '5', '10', '20', '50', '75', '100', '250', '500', '1000', '2000', '5000','6000'].map((value) => (
                    <button
                        type="button"
                        key={value}
                        onClick={() => setAmount(value)}
                        className={`amount-button ${amount === value ? 'active' : ''}`}
                    >
                        £{value}
                    </button>
                ))}
            </div>

            <div className="other-amount">
                <input
                    type="number"
                    placeholder="Other"
                    value={amount}
                    onChange={handleAmountChange}
                    className="other-input"

                />
            </div>

            <button type="submit" className="submit-button">Donate</button>
        </form >
    );
};

export default DonationForm;
