import React, { Component, Fragment, useState } from "react";
import donate from '../../Assets/unnamed.jpg'
import './Donate.css';



const DonationForm = () => {
    const [donationType, setDonationType] = useState('one-time');
    const [amount, setAmount] = useState('');

    const handleDonationChange = (e) => {
        setDonationType(e.target.value);
    };

    const handleAmountChange = (e) => {
        setAmount(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        alert(`Donation type: ${donationType}, Amount: £${amount}`);
    };

    return (


        <section className="donate-section">
            <div className="donate">
                <h1>Donate to stand up for Palestine</h1>
                <div className="row">
                    <div className="col-md-6">
                          <div className="p"></div>
                        <p>Palestinians desperately need us to take action HERE and NOW - please donate what you can to PSC</p>
                        <p>Thousands of Palestinians have already been killed in Israel’s indiscriminate bombardment.</p>
                        <p>PSC is working day and night to pressure UK leaders to call for a permanent ceasefire. Our costs are huge – please donate what you can to help fund our ongoing response, thank you.</p>
                    </div>



                    <div className="col-md-6">
                        <form onSubmit={handleSubmit} className="donation-form">
                            <h3 className="form-title">Support our work</h3>
                            <div className="donation-type">

                                <label>
                                    <input
                                        type="radio"
                                        name="donationType"
                                        value="one-time"
                                        checked={donationType === 'one-time'}
                                        onChange={handleDonationChange}

                                    />
                                    <span> One-off donation</span>

                                </label>
                                <label>
                                    <input
                                        type="radio"
                                        name="donationType"
                                        value="monthly"
                                        checked={donationType === 'monthly'}
                                        onChange={handleDonationChange}
                                    />
                                    <span className="donate-span">I'll keep supporting your work for Palestine - make my donation monthly</span>
                                </label>
                            </div>

                            <h4 className="amount-title">Choose your amount:</h4>
                            <div className="amount-options">

                                {['5', '10', '20', '50', '75', '100', '250', '500', '1000', '2000', '5000', '6000'].map((value) => (
                                    <button
                                        type="button"
                                        key={value}
                                        onClick={() => setAmount(value)}
                                        className={`amount-button ${amount === value ? 'active' : ''}`}
                                    >
                                        £{value}
                                    </button>
                                ))}
                            </div>

                            <div className="other-amount">
                                <input
                                    type="number"
                                    placeholder="Other"
                                    value={amount}
                                    onChange={handleAmountChange}
                                    className="other-input"

                                />
                            </div>

                            <button type="submit" className="submit-button">Donate</button>
                        </form >
                    </div>
                </div>
            </div>
        </section>
    );
};

export default DonationForm;
